var users_data = {};
function Get_data(){
    var xhr = new XMLHttpRequest();

    xhr.open('GET', 'http://127.0.0.1:5000/users', false);

    xhr.onload = function () {
        if (xhr.status === 200) {
            users_data = JSON.parse(xhr.responseText);
            // console.log(typeof(JSON.parse(xhr.responseText)));
        }
    };

    xhr.send();

}

Get_data();
users_data = users_data.data;
console.log(users_data[0].name);


export default function Home() {
return (
    <div className="bg-white">
    <div className="mx-auto max-w-2xl px-4  sm:px-6 lg:max-w-7xl lg:px-8">
        <div>Popular Twitter Accounts</div>
        <div className="grid grid-cols-1 gap-y-10 gap-x-6 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 xl:gap-x-8">
        {users_data.map((user) => (
            <a key={user.id} href={user.url} className="group">
                <div className="aspect-w-1 aspect-h-1 w-full overflow-hidden rounded-lg bg-gray-200 xl:aspect-w-7 xl:aspect-h-8 group-hover:opacity-75">
                    <div className="flex">
                        <img
                            src={user.profile_image_url}
                            alt={user.user}
                            className="h-3/12 w-3/12 rounded-full ml-2 mt-2"
                        />
                        <div className="mx-6 my-4">
                            <div>{user.name}</div>
                            <div>@{user.username}</div>
                        </div>
                    </div>

                    <div className="text-center">
                        <div>Followers {Math.round(user.public_metrics.followers_count/100000)/10}M</div>
                        <div>Tweets {user.public_metrics.tweet_count}</div>
                    </div>
                </div>
            </a>
        ))}
        </div>
    </div>
    </div>
)
}
