import { useLocation } from 'react-router-dom'

var tweets = {};

export default function User(props){
    const location = useLocation()
    const { from } = location.state
    var user_id = {
        "user_id": from,
    }
    console.log(user_id)
    var xhr = new XMLHttpRequest();

    xhr.open('POST', 'http://127.0.0.1:5000/user_info', false);
    xhr.setRequestHeader('Content-Type', 'application/json');
    xhr.onreadystatechange = function() {
    if (xhr.status === 200) {
        // Handle the response data
        tweets = (xhr.responseText);

    }
    else if (xhr.readyState === 4 && xhr.status !== 200) {
        // Handle any errors
        alert('Error: ' + xhr.status);
    }
    };

    xhr.send(JSON.stringify(user_id));

    // tweets = tweets.data;
    tweets = JSON.parse(tweets);
    console.log(tweets.includes.users[0].id)

    // return(<div>{from}</div>)

    return(
        <div className="bg-white">
        <div className="mx-auto max-w-2xl py-8 px-4 sm:py-8 sm:px-6 lg:max-w-7xl lg:px-8">
            <div className="text-2xl">Popular Twitter Accounts</div>
            <div className="grid grid-cols-1 mt-3 gap-y-10 gap-x-6 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 xl:gap-x-8">

                <div className="aspect-w-1 aspect-h-1 w-full overflow-hidden rounded-lg bg-gray-200 xl:aspect-w-7 xl:aspect-h-8 hover:opacity-75">
                    <div className="flex">
                        <img
                            src={tweets.includes.users[0].profile_image_url}
                            alt={tweets.includes.users[0].user}
                            className="h-3/12 w-3/12 rounded-full ml-2 mt-2"
                        />
                        <div className="mx-6 my-4">
                            <div>{tweets.includes.users[0].name}</div>
                            <div className="font-thin ">@{user.username}</div>
                        </div>
                    </div>

                    <div className="text-center">
                        <div>Followers: {Math.round(tweets.includes.users[0].public_metrics.followers_count/100000)/10}M+</div>
                        {/* <div>Tweets: {user.public_metrics.tweet_count}</div> */}
                    </div>
                </div>

                {/* <div className="aspect-w-1 aspect-h-1 w-full overflow-hidden rounded-lg bg-gray-200 xl:aspect-w-7 xl:aspect-h-8 hover:opacity-75">
                    <div className="flex">
                        <img
                            src={user.profile_image_url}
                            alt={user.user}
                            className="h-3/12 w-3/12 rounded-full ml-2 mt-2"
                        />
                        <div className="mx-6 my-4">
                            <Link to={user.username}>
                                <div>{user.name}</div>
                            </Link>
                            <div className="font-thin ">@{user.username}</div>
                        </div>
                    </div>

                    <div className="text-center">
                        <div>Followers: {Math.round(user.public_metrics.followers_count/100000)/10}M+</div>
                        {/* <div>Tweets: {user.public_metrics.tweet_count}</div> */}
                    {/* </div> */}
                {/* </div>  */}

               </div>
        </div>
        </div>
    );
}
