const products = [
    {
      id: 1,
      name: 'Earthen Bottle',
      href: '#',
      price: '$48',
      imageSrc: 'https://tailwindui.com/img/ecommerce-images/category-page-04-image-card-01.jpg',
      imageAlt: 'Tall slender porcelain bottle with natural clay textured body and cork stopper.',
    },
    {
      id: 2,
      name: 'Nomad Tumbler',
      href: '#',
      price: '$35',
      imageSrc: 'https://tailwindui.com/img/ecommerce-images/category-page-04-image-card-02.jpg',
      imageAlt: 'Olive drab green insulated bottle with flared screw lid and flat top.',
    },
    {
      id: 3,
      name: 'Focus Paper Refill',
      href: '#',
      price: '$89',
      imageSrc: 'https://tailwindui.com/img/ecommerce-images/category-page-04-image-card-03.jpg',
      imageAlt: 'Person using a pen to cross a task off a productivity paper card.',
    },
    {
      id: 4,
      name: 'Machined Mechanical Pencil',
      href: '#',
      price: '$35',
      imageSrc: 'https://tailwindui.com/img/ecommerce-images/category-page-04-image-card-04.jpg',
      imageAlt: 'Hand holding black machined steel mechanical pencil with brass tip and top.',
    },
    // More products...
]
var users_data = {};
function Get_data(){
    var xhr = new XMLHttpRequest();

    xhr.open('GET', 'http://127.0.0.1:5000/users', false);

    xhr.onload = function () {
        if (xhr.status === 200) {
            users_data = JSON.parse(xhr.responseText);
            // console.log(typeof(JSON.parse(xhr.responseText)));
        }
    };

    xhr.send();

}

Get_data();
users_data = users_data.data;
console.log(users_data[0].name);


export default function Home() {
return (
    <div className="bg-black">
    <div className="mx-auto max-w-2xl py-16 px-4 sm:py-24 sm:px-6 lg:max-w-7xl lg:px-8 bg-white">
        <h2>Products</h2>
        <div className="grid grid-cols-1 gap-y-10 gap-x-6 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 xl:gap-x-8 bg-black">
        {users_data.map((user) => (
            <a key={user.id} href={user.url} className="group">
                <div className="aspect-w-1 aspect-h-1 w-full overflow-hidden rounded-lg bg-gray-200 xl:aspect-w-7 xl:aspect-h-8">

                    <img
                        src={user.profile_image_url}
                        alt={user.description}
                        className="h-3/12 w-3/12 object-cover object-center group-hover:opacity-75"
                    />
                    <div>{user.name}</div>
                    <div>Followers {user.public_metrics.followers_count}</div>
                </div>
            </a>
        ))}
        </div>
    </div>
    </div>
)
}
