import { useLocation } from 'react-router-dom'

var tweets = {};

export default function User(props){
    const location = useLocation()
    const { from } = location.state
    var user_id = {
        "user_id": from,
    }
    console.log(user_id)
    var xhr = new XMLHttpRequest();

    xhr.open('POST', 'http://127.0.0.1:5000/user_info', false);
    xhr.setRequestHeader('Content-Type', 'application/json');
    xhr.onreadystatechange = function() {
    if (xhr.status === 200) {
        // Handle the response data
        tweets = (xhr.responseText);

    }
    else if (xhr.readyState === 4 && xhr.status !== 200) {
        // Handle any errors
        alert('Error: ' + xhr.status);
    }
    };

    xhr.send(JSON.stringify(user_id));

    // tweets = tweets.data;
    tweets = JSON.parse(tweets);
    console.log(tweets.includes.users[0].id)

    // return(<div>{from}</div>)
    var profile_img = tweets.includes.users[0].profile_image_url;
    profile_img = profile_img.substr(0, profile_img.length-11);
    profile_img = profile_img+".jpg";



    return(
        <div className="bg-gray-200">
        <div className="mx-auto max-w-2xl py-8 px-4 sm:py-8 sm:px-6 lg:max-w-7xl lg:px-8">
            <div className="grid grid-cols-1 mt-3 gap-y-10 gap-x-6 ">

                <div className="aspect-w-1 aspect-h-1 w-full overflow-hidden rounded-lg bg-white xl:aspect-w-7 xl:aspect-h-8">
                    <div className="flex">
                        <div>
                            <img
                                src={profile_img}
                                alt={tweets.includes.users[0].user}
                                className="h-8/12 w-8/12 rounded-full ml-2 mt-2 center"
                            />
                            <div className='text-center'>{tweets.includes.users[0].name}</div>
                            <div>{tweets.includes.users[0].username}</div>
                        </div>

                        <div className="mx-6 my-4">

                            <table className="border-collapse table-auto w-full">
                                <tbody className='dark:bg-slate-800'>
                                    <tr className="text-gray-800">
                                        <td className="border-b border-slate-300 dark:border-slate-700 p-4 pl-8 text-slate-500 dark:text-slate-400">Name</td>
                                        <td className="border-b border-slate-300 dark:border-slate-700 p-4 pl-8 text-slate-500 dark:text-slate-400">{tweets.includes.users[0].name}</td>
                                    </tr>
                                    <tr className="text-gray-800">
                                        <td className="border-b border-slate-300 dark:border-slate-700 p-4 pl-8 text-slate-500 dark:text-slate-400">Username</td>
                                        <td className="border-b border-slate-300 dark:border-slate-700 p-4 pl-8 text-slate-500 dark:text-slate-400">@{tweets.includes.users[0].username}</td>
                                    </tr>
                                </tbody>
                            </table>



                        </div>
                    </div>
                </div>

            </div>
        </div>
        </div>
    );
}
