import { useLocation } from 'react-router-dom'

var tweets = {};

export default function User(props){
    const location = useLocation()
    const { from } = location.state
    var user_id = {
        "user_id": from,
    }
    console.log(user_id)
    var xhr = new XMLHttpRequest();

    xhr.open('POST', 'http://127.0.0.1:5000/user_info', false);
    xhr.setRequestHeader('Content-Type', 'application/json');
    xhr.onreadystatechange = function() {
    if (xhr.status === 200) {
        // Handle the response data
        tweets = (xhr.responseText);

    }
    else if (xhr.readyState === 4 && xhr.status !== 200) {
        // Handle any errors
        alert('Error: ' + xhr.status);
    }
    };

    xhr.send(JSON.stringify(user_id));

    // tweets = tweets.data;
    tweets = JSON.parse(tweets);
    console.log(tweets.includes.users[0].id)

    // return(<div>{from}</div>)
    var profile_img = tweets.includes.users[0].profile_image_url;
    profile_img = profile_img.substr(0, profile_img.length-11);
    profile_img = profile_img+".jpg";

    return (
        <div className="bg-white p-6 rounded-lg">
          <div className="flex items-center">
            <img src={profile_img} alt={tweets.includes.users[0].name} className="w-12 h-12 rounded-full mr-4" />
            <div className="text-xl font-medium">{tweets.includes.users[0].name}</div>
          </div>
          <div className="mt-4">
            <div className="text-gray-600">{tweets.includes.users[0].name}</div>
            <div className="text-gray-600">{tweets.includes.users[0].name}</div>
          </div>
          <div className="mt-4">
            <button className="bg-indigo-500 text-white py-2 px-4 rounded-lg hover:bg-indigo-600">Edit Profile</button>
          </div>
        </div>
      );

